INSERT INTO `tb_blog` (`id_blog`, `title`, `slug_title`, `meta`, `description`, `tags`, `id_kategori`, `id_sub`, `id_user`, `status`, `image`, `create_ad`, `update_ad`) VALUES
('6184a54ee667e', 'Katalog ICE Edx 2021', 'katalog-ice-edx-2021', 'Katalog Mata Kuliah / Course ICE Institute dengan Edx Edisi 2021 disusun dengan tujuan untuk me­nyajikan informasi yang menyeluruh tentang Mata Kuliah', '<p>Katalog Mata Kuliah / Course ICE Institute dengan Edx Edisi 2021 disusun dengan tujuan untuk me&shy;nyajikan informasi yang menyeluruh tentang Mata Kuliah / Course ICE Institute dengan Edx bagi umum terutama peserta yang mendaftar di ICE Institute. Dengan demikian diharapkan berbagai pihak yang berkepentingan dapat memperoleh gambaran yang utuh tentang Mata Kuliah / Course ICE Institute dengan Edx.</p>\r\n\r\n<p>Isi Katalog Mata Kuliah / Course ICE Institute dengan Edx ini meliputi informasi tentang nama mata kuliah / course, partner mata kuliah, tipe mata kuliah, durasi mata kuliah, jadwal mata kuliah dimulai..</p>\r\n\r\n<p>Peserta yang dapat memilih mata kuliah ini merupakan peserta dengan minimun score TOEFL 500. untuk melihat detail dari mata kuliah / course dapat dilakukan dengan cara mengklik link yang tersedia pada setiap mata kuliah / courses</p>\r\n\r\n<p><iframe height=\"720\" src=\"https://docs.google.com/spreadsheets/d/e/2PACX-1vSUivn4DBS1CUbb6Rqvm1vi3bpC5w4BZsCOsPOndB-HxVGGCbGI4KfGpK3vBb3fa4G263Hyo3xG2isu/pubhtml?gid=1466322147&amp;single=true&amp;widget=true&amp;headers=false\" width=\"100%\"></iframe></p>\r\n', 'katalog,icei,ice institute,mata kuliah,Edx,icei.ac.id', 11, 18, 8, 'draft', '6184a54ee667e.png', '2021-11-05 03:30:22', '2022-04-05 01:44:20'),
('6184d7f5bb29f', 'Profil Mata Kuliah', 'profil-mata-kuliah', 'Formulir untuk partner menawarkan mata kuliah yang akan di sediakan didalam galery ICE Institute', '<p><iframe frameborder=\"0\" src=\"https://forms.zohopublic.com/universitasterbuka19/form/ProfilMataKuliah/formperma/mrwYbVVQP2k4ykSb5mb_JAPNFOdN4FgS1YWftgVT7Gs\" style=\"min-height:620px;width:99%;border:none;\"></iframe></p>\r\n', 'icei,ice intitute,merdeka belajar,kemendikbud,pjj,pembelajaran jarak jauh', 11, 21, 8, 'delete', 'default.jpg', '2021-11-04 19:06:29', '2021-11-04 19:15:47'),
('6184e37b86c13', 'Cara Klaim Sertifikat dalam Sistem ICE Institute', 'cara-klaim-sertifikat-dalam-sistem-ice-institute', 'ICE Institute, bagaimana cara klaim sertifikat dalam sistem indonesia cyber education institute', '<p><iframe frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/ETR3tj6JMm8\" title=\"YouTube video player\" width=\"560\"></iframe></p>\r\n', 'sertifikat,IceInstitute,KlaimSertifikat,Berita', 8, 14, 3, 'post', '6184e37b86c13.jpg', '2021-11-04 19:55:39', '2021-11-10 02:38:00'),
('6184e51864609', 'The 8th Cyber Education Forum (CEF)', 'the-8th-cyber-education-forum-cef', 'Webinar ICE-Institute, Cyber Education Forum (CEF)', '<p>Unit Pengembangan Pembelajaran Dalam Jaringan Indonesia (UPPDJI) atau yang dikenal dengan Indonesia Cyber Education Institute (ICE-I) mempersembahkan The 8th Cyber Education Forum (CEF) Universitas Terbuka (UT), dengan tema &quot;Machine Learning Model to Predict Student Success in Online Learning&quot;.</p>\r\n\r\n<p>Kegiatan akan berlangsung pada Rabu, 24 November 2021 pukul 10.00 WIB - Selesai, yang akan dihadiri oleh:<br />\r\n1. Prof. Paulina Pannen, M.LS.<br />\r\n2. Professor Dr. Jiyou Jia (Head of Department of Educational Technology, Graduate School of Education, Peking University)<br />\r\n3. Drs. Gatot Fatwanto Hertono, M.Sc., Ph.D. (Faculty of Math and Science Universitas Indonesia)</p>\r\n\r\n<p>Moderator oleh Fauzy Rahman Kosasih, S.S., M.Pd.</p>\r\n\r\n<p>Pendaftaran melalui laman https://sl.ut.ac.id/Pendaftaran-CEF8th, kuota peserta terbatas.</p>\r\n\r\n<p>(FREE E-CERTIFICATE)</p>\r\n\r\n<p>Acara ini bisa disaksikan melalui Zoom (https://sl.ut.ac.id/WS-CEF8th) atau melalui kanal UT TV (https://sl.ut.ac.id/StreamingCEF).</p>\r\n', 'ICE Institute,Webinar,CEF', 7, 10, 3, 'post', '6184e51864609.jpg', '2021-11-04 20:02:32', '2021-11-14 19:22:32'),
('61a49b21c8cb0', 'Katalog', 'katalog', 'katalog', '<p><iframe height=\"720\" src=\"https://explore-catalog.edx.org/?enterprise_catalog_query_titles=Education&amp;hide_cards=true&amp;hide_price=true&amp;availability=Available%20Now&amp;availability=Upcoming\" width=\"100%\"></iframe></p>\r\n', 'katalog', 11, 18, 8, 'delete', 'default.jpg', '2021-11-28 21:19:29', '2021-11-28 21:21:30'),
('61a6d42c6e505', 'International Symposium On Open, Distance, and E-Learning 2021', 'international-symposium-on-open-distance-and-e-learning-2021', 'ICE Institute berpartisipasi dalam kegiatan International Symposium On Open, Distance, and E-Learning 2021 yang di adakan secara virtual oleh Kementerian Pendidikan, Kebudayaan, Riset, dan Teknologi Republik Indonesia', '<p>Halo sahabat ICE, Hadirilah Virtual Booth International Symposium On Open, Distance, and E-Learning (ISODEL) ICE Institute pada tanggal 1-3 Desember 2021.</p>\r\n\r\n<p>ISODEL 2021 menyediakan platform interdisipliner untuk peneliti, akademisi, pendidik, praktisi, pembuat kebijakan, dan pemangku kepentingan yang ingin meningkatkan kualitas terbuka, jarak, dan pembelajaran melalui bertukar ide dalam tren baru yang muncul yang lebih membutuhkan lebih banyak fokus dan eksposur.</p>\r\n\r\n<p>Jangan lupa untuk hadir secara virtual ke dalam booth ICE Institute<br />\r\n<strong>Alamat: <a href=\"https://isodelexpo.kemdikbud.go.id\" target=\"_blank\">isodelexpo.kemdikbud.go.id</a><br />\r\nLokasi: EduTech Hall Booth ICE-I</strong></p>\r\n\r\n<p>Ada Informasi penting yang tersedia dari ICE Institute, dan yang pasti akan ada Quiz dan juga hadiah menarik untuk kalian yang mengikuti kegiatan pada booth ICE Institute.</p>\r\n\r\n<p>Yuk jangan ragu untuk menghadiri acara ini.</p>\r\n\r\n<p>Salam Merdeka Belajar untuk Semua</p>\r\n', 'icei,iceinstitute,indonesiacybereducationinstitute,isodel,kemdikbud', 8, 14, 8, 'post', '61a6d42c6e505.jpg', '2021-12-01 01:47:24', '2021-12-01 01:47:24'),
('61b05cf881de9', 'Katalog Mata Kuliah ICE - I 2022.1', 'katalog-mata-kuliah-ice-i-20221', 'Katalog Mata Kuliah / Course ICE Institute Edisi 2022 disusun dengan tujuan untuk me­nyajikan informasi yang menyeluruh tentang Mata Kuliah / Course ICE Institute bagi umum terutama peserta yang mendaftar di ICE Institute', '<p>Katalog Mata Kuliah / Course ICE Institute Edisi 2022&nbsp;disusun dengan tujuan untuk me&shy;nyajikan informasi yang menyeluruh tentang Mata Kuliah / Course ICE Institute bagi umum terutama peserta yang mendaftar di ICE Institute. Dengan demikian diharapkan berbagai pihak yang berkepentingan dapat memperoleh gambaran yang utuh tentang Mata Kuliah / Course ICE Institute.</p>\r\n\r\n<p>Isi Katalog Mata Kuliah / Course ICE Institute ini meliputi informasi tentang nama mata kuliah / course, institusi penyedia&nbsp;mata kuliah, tipe mata kuliah, bobot mata kuliah.</p>\r\n\r\n<p>Peserta yang dapat memilih mata kuliah ini merupakan peserta yang telah mendaftar program ke ICE Institute melalui institusi masing masing.</p>\r\n\r\n<p><iframe height=\"700\" src=\"https://docs.google.com/spreadsheets/d/e/2PACX-1vRXGypkX4Hu3zl4YYBUuhkX9BKSX-ysftj6iDtpNqR2l-4Zl2oSPXUj-a-sMsSHKU41JLioMtrUkPBb/pubhtml?gid=1048712611&amp;single=true&amp;widget=true&amp;headers=false\" width=\"100%\"></iframe></p>\r\n', 'iceinstitute,ice-i,indonesiacybereducationinstitute,katalogiceinstitute,katalogmatakuliah', 11, 18, 8, 'post', '61b05cf881de9.png', '2021-12-07 19:21:28', '2021-12-07 22:03:20'),
('61c596bb12d3a', 'Perpanjangan Pendaftaran Beasiswa ICE Institute 2022', 'perpanjangan-pendaftaran-beasiswa-ice-institute-2022', 'Pendaftaran Beasiswa Mendikbudristek Diperpanjang sampai 10 Januari 2022 loh!!', '<p>&nbsp;</p>\r\n\r\n<h3>Halo teman - teman ICE!<br />\r\n<br />\r\nPendaftaran Beasiswa Mendikbudristek Diperpanjang sampai 10 Januari 2022 loh!!<br />\r\n<br />\r\nIni adalah persyaratan buat kamu dan perguruan tinggi kamu untuk mendaftar ke ICE<br />\r\n<br />\r\nUntuk Perguruan Tinggi:<br />\r\n-Melampirkan Pakta Integritas (https://info-icei.ut.ac.id/berita/pakta-integritas) sebanyak 1 lembar<br />\r\n-Surat Rekomendasi yang melampirkan data mahasiswa berupa Nama, NIM, no. HP, dan Email Resmi mahasiswa dari Perguruan Tinggi asal<br />\r\n<br />\r\nUntuk Mahasiswa:<br />\r\n-Minimum semester 5<br />\r\n-Mendaftar melalui perguruan tinggi masing - masing<br />\r\n-Memilih max. 5 mata kuliah dalam negeri (https://bit.ly/katalog-iceinstitute)<br />\r\n-Memilih max. 2 mata kuliah edX (https://bit.ly/katalogedx-ice)</h3>\r\n\r\n<h3>Panduan Pendaftaran dapat teman teman lihat pada file dibawah ini.<br />\r\n<iframe height=\"480\" src=\"https://drive.google.com/file/d/1_Jb_D5x4mUt8a52DflGNkEjA05qkyXXx/preview\" width=\"640\"></iframe><br />\r\nKami tunggu yaa semangat belajarnya</h3>\r\n\r\n<h3><br />\r\n<br />\r\nSalam,<br />\r\nMerdeka Belajar Untuk Semua</h3>\r\n', 'beasiswa2022,pendaftaran,iceinstitute,ice-i,ice,indonesiacybereducationinstitute,beasiswa', 8, 14, 8, 'post', '61c596bb12d3a.png', '2021-12-23 21:45:31', '2021-12-28 04:14:20'),
('61d2d915cfa91', 'Katalog Microcredential Game Developer', 'katalog-microcredential-game-developer', 'microcredential game developer', '<p><span style=\"font-size:16px\">Halo teman-teman ICE Institute.<br />\r\nAda program baru dari Kampus Merdeka Kemendikbudristek lho, yaitu Program Studi Independen Bersertifikat (SIB) Microcredential Game Developer</span></p>\r\n\r\n<p><iframe align=\"middle\" frameborder=\"0\" height=\"560\" scrolling=\"yes\" src=\"https://drive.google.com/file/d/1hIq-wfZromug-eybfTRVNwIu1nU8RxpY/preview\" width=\"560\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Program Microcredential Game Developer memiliki 5 (lima) streams (peminatan) dan 1 (satu) proyek akhir*, yaitu: </span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Game <a name=\"_Hlk91665929\">Artist</a></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Game Design</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Game Programmer</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Game Educational Developer</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Game Project Management</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Capstone Project*</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Masing-masing stream memiliki durasi pembelajaran selama 2,5 bulan dengan bobot masing-masing course adalah 15 SKS. Bagi mahasiswa yang lulus dari masing-masing microcredential course maka akan mendapatkan sertifikat dari stream yang diambil.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Sedangkan untuk Capstone Project, durasi untuk proyek akhir ini adalah selama 1,5 bulan dengan bobot 5 SKS. Bagi mahasiwa yang lulus dari proyek akhir ini maka akan mendapatkan sertifikat kelulusan program microcredential game developer dengan bobot total 20 SKS. </span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Luaran dari Program Microcredential Game Developer ini adalah 100 game yang siap pakai dalam beragam platform game ecosystem. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Visi dan Misi ICE Institute</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">ICE Institute diharapkan mampu berperan dalam peningkatan perluasan akses dan pemerataan kualitas pendidikan untuk mendorong Angka Partisipasi Pendidikan (APK) menjadi 40%. ICE Institute juga akan membantu pemenuhan kebutuhan lifelong learner melalui penyediaan sistem dan content pembelajaran bermutu yang dapat diakses kapanpun dan dimanapun.</span></p>\r\n\r\n<p><strong><span style=\"font-size:16px\">Perguruan Tinggi Penyelenggara Program</span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><img src=\"https://lh3.googleusercontent.com/VA8Rt3l86r-9XLsJMaAEgb2ZnBITgx1Drtd7uHpofzP4CGAB45L1W7x1vOTkz6LXGjTn7jZaTj0rMXTteqDsvPeyUmBl5N9dfXUIqRky1DzM6D9XqhrLJsh4Qi5pdFDxVGnrYjXU\" style=\"height:227px; margin-left:0px; margin-top:0px; width:650px\" /></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><strong><span style=\"font-size:16px\">Pendaftaran Mahasiswa Peserta Program Microcredential Game Developer</span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Bagi mahasiswa yang berminat untuk bergabung dalam program ini, tersedia 3000 slot per tahun atau dengan kata lain 1500 slot per semester yang akan dibagi ke dalam masing-masing stream (peminatan).</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-size:12.0pt\">Pendaftaran dibuka melalui laman Kampus Merdeka Kemendikbudristek pada alamat <span style=\"color:#0563c1\"><u><a href=\"https://bit.ly/KM-GameDeveloper\" style=\"color:#0563c1; text-decoration:underline\">https://bit.ly/KM-GameDeveloper</a></u></span>. </span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:16px\"><strong>Katalog Microcredential Game Developer&nbsp;</strong> </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">Berikut ini merupakan katalog untuk mahasiswa:</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><iframe align=\"middle\" frameborder=\"0\" height=\"720\" scrolling=\"yes\" src=\"https://drive.google.com/file/d/12-XRnAgdQxBXpH29S6khr9Mir2lkRvzP/preview\" width=\"560\"></iframe></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">unduh katalog <a href=\"https://drive.google.com/file/d/12-XRnAgdQxBXpH29S6khr9Mir2lkRvzP/view?usp=sharing\"><span style=\"color:#e74c3c\">disini</span></a></p>\r\n', 'microcredential game developer,beasiswa', 8, 14, 3, 'post', '61d2d915cfa91.png', '2022-01-02 23:08:05', '2022-01-10 03:43:14'),
('61dc5099f2c14', 'Pedoman Dosen dan Mentor Penyelenggaraan Program Microcredential Game Developer', 'pedoman-dosen-dan-mentor-penyelenggaraan-program-microcredential-game-developer', 'pedoman penyelenggaraan program', '<p><strong><span style=\"font-size:16px\">Pedoman Penyelenggaraan Program untuk Dosen atau Mentor</span></strong></p>\r\n\r\n<p>Berikut ini merupakan pedoman penyelenggaraan program microcredential game developer untuk dosen atau mentor:</p>\r\n\r\n<p><iframe align=\"middle\" frameborder=\"0\" height=\"560\" scrolling=\"yes\" src=\"https://drive.google.com/file/d/1TQixISzfWogM4C6ftX4ftsrUbS7wPOw1/preview\" width=\"560\"></iframe></p>\r\n\r\n<p>unduh pedoman <a href=\"https://drive.google.com/file/d/1TQixISzfWogM4C6ftX4ftsrUbS7wPOw1/view?usp=sharing\"><span style=\"color:#e74c3c\">disini</span></a></p>\r\n', 'microcredential game developer,beasiswa', 8, 14, 3, 'post', '61dc5099f2c14.png', '2022-01-10 03:28:25', '2022-01-10 03:43:48'),
